class RHSFile{
    constructor(path){
        console.log("loading " + path);

        //Test
        var rawFile = new XMLHttpRequest();
        var localPath = "file:///C:/Users/tank6/Desktop/React/brainwave_webviewer/sample/" + path;

        /*
        rawFile.onreadystatechange = function ()
        {
            if(rawFile.readyState === 4)
            {
                if(rawFile.status === 200 || rawFile.status == 0)
                {
                    var reader = new FileReader();
                    
                    reader.onload = function(e){
                        console.log(e.target.result);
                    };
                    reader.readAsBinaryString(rawFile.response);
                }

                rawFile.send(null);
            }
            else{
                alert('Failed to read file ' + path);
            }
        }
        */

        rawFile.addEventListener("load", this.onLoad);
        rawFile.open("GET", localPath, true);
        rawFile.overrideMimeType("text/plain; charset=x-user-defined");

        //reader.readAsBinaryString("intan_save_210107_151441.rhs");

        //Parse
    }

    onLoad (){
        if(this.readyState === 4)
            {
                if(this.status === 200 || this.status === 0)
                {
                    var reader = new FileReader();
                    
                    reader.onload = function(e){
                        console.log(e.target.result);
                    };
                    reader.readAsBinaryString(this.response);
                }
            }
            else{
                alert('Failed to read file');
            }
    }
}

export default RHSFile;
