import React from 'react';
import ScrollBar from './ScrollBar';

class CanvasGraph extends React.Component{
    constructor(props){
        super(props);
        this.canvasRef = React.createRef();
        this.canvasContext = null;
        this.scrollBarRef = null;
        this.isPlaying = false;
        this.textWidth = 100;

        //Bind Functions
        this.play = this.play.bind(this);
        this.stop = this.stop.bind(this);
        this.setoffset = this.setoffset.bind(this);
        this.setpeaks = this.setpeaks.bind(this);

        var channelCount = props.channels;
        this.peakArray = [];
        this.peakOffset = props.count / -2;
        this.peakMaxOffset = 0;
        this.peakMinOffset = props.count / -2;

        //Create Test Samples
        var c;
        var i;
        for (c = 0; c < channelCount; c++){
            this.peakArray.push([]);

            for (i = 0; i < 100000; i++){
                this.peakArray[c].push(0);
            }
        }

        this.peakMaxOffset = this.peakArray[0].length - this.props.count / 2;
    }

    componentDidMount() {
        this.canvasContext = this.canvasRef.current.getContext('2d', {alpha: false});
        this.canvasContext.fillStyle = 'black';
        this.canvasContext.lineWidth = 1;
        this.canvasContext.shadowBlur = 0;

        this.canvasContext.font = (this.props.height / 2) + 'px malgun gothic';
        this.play();
    }

    componentWillUnmount() {
        this.stop();
    }

    //Play
    play (){
        //Rewind
        if (this.peakOffset === this.peakMaxOffset){
            this.peakOffset = this.peakMinOffset;
        }
        
        this.timer = setInterval(()=>{this.update(); this.draw();}, 5);
        this.isPlaying = true;

        if (this.props.onPlayStateChanged !== undefined)
            this.props.onPlayStateChanged();
    }
    
    stop(){
        if (this.timer == null)
            return;

        clearInterval(this.timer);
        this.timer = null;
        this.isPlaying = false;

        if (this.props.onPlayStateChanged !== undefined)
            this.props.onPlayStateChanged();
    }

    togglePlay (){
        if (this.isPlaying)
            this.stop();
        else
            this.play();
    }
    
    //Controls
    setpeaks(arr){
        this.peakArray = arr;
        this.peakOffset = this.peakMinOffset;
        this.peakMaxOffset = this.peakArray[0].length - this.props.count / 2;

        this.scrollBarRef.setHandlePosition(0);
        this.draw();

        if (this.props.onOffsetChanged !== undefined)
            this.props.onOffsetChanged(0);
    }

    setoffset(pos){
        this.peakOffset = Math.floor((this.peakMaxOffset + (this.props.count / 2)) * pos) - (this.props.count / 2);
        this.draw();

        if (this.props.onOffsetChanged !== undefined)
            this.props.onOffsetChanged(pos);
    }

    //Play Loop
    update (){
        this.peakOffset += this.props.speed;

        if (this.peakOffset > this.peakMaxOffset){
            this.peakOffset = this.peakMaxOffset;
            this.stop();
        }

        if (this.props.onOffsetChanged !== undefined)
            this.props.onOffsetChanged(this.peakOffset / this.peakMaxOffset);

        if (this.scrollBarRef != null)
            this.scrollBarRef.setHandlePosition((this.peakOffset + (this.props.count / 2)) / (this.peakMaxOffset + (this.props.count / 2)));
    }

    draw (){
        if (this.peakArray[0].length < 2)
            return;
        
        var channelCount = this.props.channels > this.peakArray.length ? this.peakArray.length : this.props.channels;

        var len = this.props.count < this.peakArray[0].length ? this.props.count : this.peakArray[0].length;
        var width = this.canvasRef.current.offsetWidth - this.textWidth;
        var height = this.canvasRef.current.offsetHeight;
        var halfHeight = this.props.height / 2;
        var widthStep = width / this.props.count;

        var startX = widthStep * Math.min(this.peakOffset, 0) * -1 + this.textWidth;
        var startY = 0;
        
        this.canvasContext.strokeStyle = this.props.strokeColor;
        this.canvasContext.fillStyle = 'black';
        this.canvasContext.fillRect(0, 0, width + this.textWidth, height);
        //this.canvasContext.clearRect(0, 0, width, height);

        var currentIdx = 0;
        var x = 0;
        var y = 0;

        this.canvasContext.fillStyle = 'white';
        this.canvasContext.beginPath();
        for (var c = 0; c < channelCount; c++){
            startY = halfHeight * (c * 2 + 1) + (this.props.margin * c);
            
            //Draw channel text
            this.canvasContext.fillText('channel_' + (c + 1), 0, startY + (halfHeight / 2));
            this.canvasContext.moveTo(startX, (this.peakArray[c][Math.max(this.peakOffset, 0)] * halfHeight) + startY);

            //Draw graph
            for(var i = 1; i < len; i++){
                currentIdx = Math.max(i + this.peakOffset, i);
                if (currentIdx > this.peakArray[c].length)
                    continue;

                x = (widthStep * i) + startX + 0.1;
                y = (this.peakArray[c][currentIdx] * halfHeight) + startY + 0.1;
                this.canvasContext.lineTo(x, y);
            }
        }
        this.canvasContext.stroke();

        this.canvasContext.beginPath();
        this.canvasContext.strokeStyle = 'red';
        this.canvasContext.moveTo(width / 2 + this.textWidth, 0);
        this.canvasContext.lineTo (width / 2 + this.textWidth, height);
        this.canvasContext.stroke();
    }

    getCanvas(){
        return this.canvasRef.current;
    }

    render(){
        var style={
            display: 'flex',
            flexDirection: 'column'
        };
        
        this.peakMaxOffset = this.peakArray[0].length - this.props.count / 2;
        this.peakMinOffset = this.props.count / -2;

        if (this.canvasRef.current != null)
            this.draw();

        return (
        <div style={style}>
            <canvas ref={this.canvasRef} 
            width={this.props.width} height={this.props.channels * (this.props.height + this.props.margin)}/>
            <ScrollBar ref={ref=>{this.scrollBarRef = ref;}}
            width={this.props.width} height='20'
            handleWidth={this.props.count / (this.peakArray[0].length + this.props.count / 2)}
            onDragStart={this.stop}
            onDrag={this.setoffset}/>
        </div>);
    }
}

export default CanvasGraph;
