import React from 'react';
import logo from '.././logo.svg';
import './viewer.css';
import CanvasGraph from './CanvasGraph';

class WaveGraphPlayer extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            channels: 8,
            count: 5000,
            canvasWidth: 1600,
            isPlaying: false
        };

        this.graphRef = null;

        this.onPlayStateChanged = this.onPlayStateChanged.bind(this);
        this.onKeyDown = this.onKeyDown.bind(this);
        this.onPlayButtonClicked = this.onPlayButtonClicked.bind(this);
        this.onResize = this.onResize.bind(this);
    }

    componentDidMount(){
        this.setState({isPlaying: this.graphRef.isPlaying});
        this.onResize();
        //fetch('http://localhost:3000/api');
        window.addEventListener("resize", this.onResize);
        window.addEventListener("keydown", this.onKeyDown);
    }

    componentWillUnmount(){
        window.removeEventListener("resize", this.onResize);
        window.removeEventListener("keydown", this.onKeyDown);
    }

    onPlayStateChanged(){
        if (this.graphRef === null)
            return;

        this.setState({isPlaying: this.graphRef.isPlaying});
    }

    onPlayButtonClicked(e){
        this.graphRef.togglePlay();
    }

    onKeyDown(e){
        if (e.keyCode === 32)
            this.graphRef.togglePlay();
    }

    onResize(){
        this.setState({
            canvasWidth: this.graphRef.getCanvas().getBoundingClientRect().width
        });
    }

    render(){
        var btnStyle={
            height: '50px',
            width: '50px'
        }
        return(
        <div>
            <CanvasGraph ref={ref=>{this.graphRef = ref;}}
            width={this.state.canvasWidth} height='50'
            channels='8' invertal='10' count='5000'
            strokeColor="#FFFFFF"
            onPlayStateChanged={this.onPlayStateChanged}/>
            <button onClick={this.onPlayButtonClicked} style={btnStyle}>
                {this.state.isPlaying ? "stop" : "play"}
            </button>
        </div>);
    }
}

class FileList extends React.Component{
    constructor(props){
        super(props);
        this.onFileSelected = this.onFileSelected.bind(this);

        //Request file list to back-end
        //fetch("/files");

        this.state = {
            fileList: ["1.file", "2.file", "3.file"],
            selectedFile: ""
        };
    }

    onFileSelected (){
        var selected = document.getElementById("fileList").value;

        if (selected === this.state.selectedFile)
            return;

        this.setState({selectedFile: selected});
        console.log("selected : " + selected);
        //Load File
    }

    render(){
        var options = [];
        for (var i = 0; i < this.state.fileList.length; i++){
             options.push(
                 <option key={i} value={this.state.fileList[i]}>{this.state.fileList[i]}</option>
             )       
        }

        return (
        <select id="fileList" className="files"  onDoubleClick={this.onFileSelected} multiple>
            {options}
        </select>);
    }
}

class PlayerController extends React.Component{

    constructor(props){
        super(props);
        
        this._onCountChanged = this._onCountChanged.bind(this);
        this._onChannelChanged = this._onChannelChanged.bind(this);
    }

    _onCountChanged (){
        
    }

    _onChannelChanged (){

    }

    render(){
        return (
        <div className="player_Controller">
            <span>아아아ㅏㅏ아아아메뉴우우ㅜ우ㅡ</span>
            
            <br/>
            <br/>
            <span>확대</span>
            <select id="count_select" onChange={this._onCountChanged}>
                <option value='500'>500</option>
                <option value='1000'>1000</option>
                <option value='2500'>2500</option>
                <option value='5000'>5000</option>
            </select>

            <br/>
            <span>채널</span>
            <select id="channel_select" onChange={this._onChannelChanged}>
                <option value='1'>1</option>
                <option value='2'>2</option>
                <option value='4'>4</option>
                <option value='8'>8</option>
                <option value='16'>16</option>
            </select>
        </div>);
    }
}

class Viewer extends React.Component {
    constructor(props){
        super(props);
        this.playerRef = React.createRef();
    }
    
    onCountChanged (v){

    }

    onChannelChanged(v){

    }

    render(){
        return (
            <div className="Viewer">
                <div className="Viewer_Menu">
                    <PlayerController
                    onCountChanged={this.onCountChanged}
                    onChannelChanged={this.onChannelChanged}/>
                    <FileList/>
                </div>
                <div className="Viewer_Graph">
                    <img src={logo} className="logo" alt="logo" 
                    width='100px' height='100px'/>
                    <WaveGraphPlayer />
                </div> 
            </div>);
    }
}

export default Viewer;
