class RHSFile{
    constructor(){
    }

    load (path){
        console.log("loading " + path);
        this.filestream = null;
        this.onProgress = this.onProgress.bind(this);
        this.onLoad = this.onLoad.bind(this);
        this.onError = this.onError.bind(this);

        //Test
        var localPath = "http://localhost:3001/sample/" + path;

        this.request = new XMLHttpRequest();
        this.request.open("GET", localPath, true);
        this.request.onprogress = this.onProgress;
        this.request.onload = this.onLoad;
        this.request.onerror = this.onError;
        this.request.overrideMimeType("text/plain; charset=x-user-defined");
        this.request.send(null);
    }

    //#####Events#####
    onProgress(e){
        var percentage = e.loaded / e.total * 100;
        console.log(e.loaded + ' / ' + e.total + " (" + percentage + '%)');
    }

    onLoad (e){
        if(this.request.readyState === 4) {
            if(this.request.status === 200 || this.request.status === 0) {
                this.filestream = this.request.responseText;
                console.log('file loaded successfully');
            }
        }
        else{
            alert('Failed to read file');
        }

        //Validate Magin-number
        var magicNum = this.getMultiBytes(0, 4);
        for (var i = 0; i < magicNum.length; i++){
            console.log('byte[' + i + '] : ' + magicNum[i].toString(16));
        }
        var version = this.getInt16(4);
        console.log('version : ' + version);
    }

    onError (e){
        console.log('Error : ' + this.request.statusText);
    }
    
    //#####Utilities#####
    getByte (index) {
        if (this.filestream)
            return this.filestream.charCodeAt(index) & 0xff;
        else
            return 0x00;
    }

    //little-endian
    getMultiBytes(startIdx, count){
        var array = [];
        for(var i = count - 1; i > -1; i--){
            array.push(this.getByte(i));
        }
        
        return array;
    }

    getInt16(index) {
        var bytes = this.getMultiBytes(index, 2);
        var value = 0;

        for (var i = 0; i < bytes.length; i++){
            value = value | (bytes[i] << (i * 8));
        }

        return value;
    }
}

export default RHSFile;
