class RHSFile{
    constructor(){
        this.dataView = null;
        this.onProgress = this.onProgress.bind(this);
        this.onLoad = this.onLoad.bind(this);
        this.onError = this.onError.bind(this);
    }

    load (path){
        console.log("loading " + path);

        //Test
        this.request = new XMLHttpRequest();
        this.request.open("GET", "http://localhost:3001/sample/" + path, true);
        this.request.responseType = "arraybuffer";
        this.request.onprogress = this.onProgress;
        this.request.onload = this.onLoad;
        this.request.onerror = this.onError;
        this.request.overrideMimeType("text/plain; charset=x-user-defined");
        this.request.send(null);
    }

    //#####Events#####
    onProgress(e){
        var percentage = e.loaded / e.total * 100;
        console.log(e.loaded + ' / ' + e.total + " (" + percentage + '%)');
    }

    onLoad (e){
        if(this.request.readyState === 4) {
            if(this.request.status === 200 || this.request.status === 0) {
                this.dataView = new DataView(this.request.response);
                console.log('file loaded successfully');
                this.parse();
            }
        }
        else{
            alert('Failed to read file');
        }
    }

    onError (e){
        console.log('Error : ' + this.request.statusText);
    }

    parse (){
        //Validate Magin-number
        var magicNum = [0xD6, 0x91, 0x27, 0xAC];
        var nmValues = this.getMultiBytes(0, 4);
        for (var i = 0; i < nmValues.length; i++){
            console.log('byte[' + i + '] : ' + nmValues[i].toString(16));
            
            if (magicNum[i] !== nmValues[i]){
                console.log('Wrong magic number detected');
                return;
            }
        }
        console.log('Magic number found');

        //Load header
        console.log('version: ' + this.dataView.getInt16(4, true));
        console.log('sample rate: ' + this.dataView.getFloat32(8, true));
        console.log('DSP Enabled: ' + this.dataView.getInt16(12, true));
    }
    
    //#####Utilities#####
    //little-endian
    getMultiBytes(startIdx, count) {
        var array = [];
        for(var i = count - 1; i > -1; i--){
            array.push(this.dataView.getUint8(startIdx + i));
        }
        
        return array;
    }
}

export default RHSFile;
